PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer not null primary key autoincrement, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer not null primary key autoincrement, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "password_resets" ("email" varchar not null, "token" varchar not null, "created_at" datetime);
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer not null primary key autoincrement, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime default CURRENT_TIMESTAMP not null);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer not null primary key autoincrement, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "pelapor" ("id" integer not null primary key autoincrement, "nim" varchar not null, "nama" varchar not null, "email" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "laboratorium" ("id" integer not null primary key autoincrement, "nomor" varchar not null, "image" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "barang" ("id" integer not null primary key autoincrement, "laboratorium_id" integer not null, "jenis_barang_id" integer not null, "nomor_barang" varchar not null, "created_at" datetime, "updated_at" datetime, foreign key("laboratorium_id") references "laboratorium"("id") on delete cascade, foreign key("jenis_barang_id") references "jenis_barang"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "jenis_barang" ("id" integer not null primary key autoincrement, "nama_barang" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "detail_barang" ("id" integer not null primary key autoincrement, "barang_id" integer not null, "pelapor_id" integer, "kondisi" varchar check ("kondisi" in ('baik', 'rusak', 'hilang')) not null, "keterangan" varchar, "approved" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, "laporan_id" integer, foreign key("barang_id") references "barang"("id") on delete cascade, foreign key("pelapor_id") references "pelapor"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "laporan" ("id" integer not null primary key autoincrement, "pelapor_id" integer not null, "di_lihat" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("pelapor_id") references "pelapor"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "notifications" ("id" varchar not null, "type" varchar not null, "notifiable_type" varchar not null, "notifiable_id" integer not null, "data" text not null, "read_at" datetime, "created_at" datetime, "updated_at" datetime, primary key ("id"));
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "password_resets_email_index" on "password_resets" ("email");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE INDEX "notifications_notifiable_type_notifiable_id_index" on "notifications" ("notifiable_type", "notifiable_id");
COMMIT;
